import React, {useId} from 'react'


function InputBox({
  label,
  amount,
  onAmountChange,
  onCurrencyChange,
  currencyOptions = [],
  selectCurrency = "usd",
  currencyDisables = false,
  amountDisabled = false,
  className = "",
}) {

    const amountInputId = useId()
  return (
      <div className={`bg-white p-3 rounded-lg text-sm flex ${className}`}>
          <div className="w-1/2">
              <label htmlFor={amountInputId} className="text-black/40 mb-2 inline-block">
                  {label}
              </label>
              <input
                    id={amountInputId}
                  className="outline-none w-full bg-transparent py-1.5"
                  type="number"
                  placeholder="Amount"
                  disabled={amountDisabled}
                  value={amount}
                  onChange={(e)=> onAmountChange && onAmountChange((e)=>Number(e.target.value))}
              />
          </div>
          <div className="w-1/2 flex flex-wrap justify-end text-right">
              <p className="text-black/40 mb-2 w-full">Currency Type</p>
              <select
                  className="rounded-lg px-1 py-1 bg-gray-100 cursor-pointer outline-none"    

                  value={selectCurrency}
                  onChange={onCurrencyChange && onCurrencyChange((e)=>e.target.value)}
                  disabled ={currencyDisables}              >

                      {currencyOptions.map((currency)=>{<option key={currency} value={currency}>{currency}
                      </option>
              })}
              </select>
          </div>
      </div>
  );
}

export default InputBox;
